import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import SvgIcon from '@/components/icons/SvgIcon.vue';
export default {
  name: "MantencionesCards",
  components: {
    SvgIcon
  },
  props: {
    mantenciones: {
      type: Array,
      default: () => []
    }
  },
  data() {
    return {
      search: "",
      debouncedSearch: "",
      currentPage: 1,
      rowsPerPage: 5
    };
  },
  computed: {
    filteredData() {
      if (!this.debouncedSearch) return this.mantenciones;
      return this.mantenciones.filter(item => item.patente.toLowerCase().includes(this.debouncedSearch));
    },
    totalPages() {
      return Math.max(1, Math.ceil(this.filteredData.length / this.rowsPerPage));
    },
    paginatedData() {
      const start = (this.currentPage - 1) * this.rowsPerPage;
      return this.filteredData.slice(start, start + this.rowsPerPage);
    },
    totalVerde() {
      return this.mantenciones.filter(m => m.estado === 'OK').length;
    },
    totalAmarillo() {
      return this.mantenciones.filter(m => m.estado === 'Próxima mantención').length;
    },
    totalRojo() {
      return this.mantenciones.filter(m => m.estado === 'Requiere mantención').length;
    }
  },
  watch: {
    search(val) {
      clearTimeout(this._debounceTimeout);
      this._debounceTimeout = setTimeout(() => {
        this.debouncedSearch = val.trim().toLowerCase();
        this.currentPage = 1;
      }, 300);
    },
    filteredData() {
      if (this.currentPage > this.totalPages) {
        this.currentPage = 1;
      }
    }
  },
  methods: {
    goToPage(page) {
      if (page >= 1 && page <= this.totalPages) this.currentPage = page;
    },
    formatOdometro(valor) {
      // Formato con separador de miles y dos decimales
      const num = parseFloat(valor);
      return isNaN(num) ? '0.00' : num.toLocaleString('es-CL', {
        minimumFractionDigits: 2,
        maximumFractionDigits: 2
      });
    },
    clearSearch() {
      this.search = "";
      this.debouncedSearch = "";
    }
  }
};
