import { ref } from 'vue';
import EmpresasTable from '@/components/Empresas/EmpresasTable.vue';
import AgregarEmpresasModal from '@/components/Empresas/AgregarEmpresasModal.vue';
import EditarEmpresasSidebar from '@/components/Empresas/EditarEmpresasSidebar.vue';

// Datos iniciales

export default {
  __name: 'EmpresasView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const customers = ref([{
      id: 1,
      createdAt: '2025-07-30',
      name: 'Demo Combustible',
      rut: '76.145.230-9',
      contact: 'Marcelo Díaz',
      city: 'Santiago'
    }, {
      id: 2,
      createdAt: '2025-07-23',
      name: 'Industrial Glover S.P.A.',
      rut: '77.239.684-2',
      contact: 'Patricia González',
      city: 'Lautaro'
    }, {
      id: 3,
      createdAt: '2025-05-29',
      name: 'Cuenta Demo',
      rut: '76.003.412-5',
      contact: 'Claudia Muñoz',
      city: 'Temuco'
    }
    // ...
    ]);
    const templates = ref([{
      id: 1,
      name: 'Básico',
      description: 'Acceso limitado a reportes y panel general.'
    }, {
      id: 2,
      name: 'Profesional',
      description: 'Incluye reportes y comentarios.'
    }, {
      id: 3,
      name: 'Avanzado',
      description: 'Incluye dashboards personalizados y reportes completos.'
    }, {
      id: 4,
      name: 'Full',
      description: 'Todas las funciones habilitadas.'
    }]);
    const searchTerm = ref('');
    const selectedIds = ref([]);
    const showAddModal = ref(false);
    const showEditSidebar = ref(false);
    const editingCompany = ref({});
    const copiedId = ref(null);

    // Métodos
    const cancel = () => showAddModal.value = false;
    const addCompany = company => {
      const id = Date.now();
      const createdAt = new Date().toISOString().slice(0, 10);
      customers.value.push({
        id,
        createdAt,
        ...company
      });
      showAddModal.value = false;
    };
    const editCustomer = company => {
      editingCompany.value = {
        ...company
      };
      showEditSidebar.value = true;
    };
    const updateCompany = company => {
      const index = customers.value.findIndex(c => c.id === company.id);
      if (index !== -1) customers.value[index] = company;
      showEditSidebar.value = false;
    };
    const deleteSelected = () => {
      customers.value = customers.value.filter(c => !selectedIds.value.includes(c.id));
      selectedIds.value = [];
    };
    const copyCustomerData = customer => {
      const data = `Nombre: ${customer.name}\nRUT: ${customer.rut}\nContacto: ${customer.contact}`;
      navigator.clipboard.writeText(data);
    };
    const __returned__ = {
      customers,
      templates,
      searchTerm,
      selectedIds,
      showAddModal,
      showEditSidebar,
      editingCompany,
      copiedId,
      cancel,
      addCompany,
      editCustomer,
      updateCompany,
      deleteSelected,
      copyCustomerData,
      ref,
      EmpresasTable,
      AgregarEmpresasModal,
      EditarEmpresasSidebar
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
