import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import { ref } from 'vue';
import CompanyTable from './CompanyTable.vue';
import AddCompanyModal from './AddCompanyModal.vue';
import EditCompanySidebar from './EditCompanySidebar.vue';
const DEFAULT_TEMPLATE_ID = 1;

// Datos iniciales

export default {
  __name: 'EmpresasView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const customers = ref([{
      id: 1,
      createdAt: '2025-07-30',
      name: 'Demo Combustible',
      rut: '76.145.230-9',
      contact: 'Marcelo Díaz',
      city: 'Santiago'
    }, {
      id: 2,
      createdAt: '2025-07-23',
      name: 'Industrial Glover S.P.A.',
      rut: '77.239.684-2',
      contact: 'Patricia González',
      city: 'Lautaro'
    }
    // ... más empresas
    ]);
    const templates = ref([{
      id: 1,
      name: 'Básico',
      description: 'Acceso limitado a reportes y panel general.'
    }, {
      id: 2,
      name: 'Profesional',
      description: 'Incluye reportes y comentarios.'
    }, {
      id: 3,
      name: 'Avanzado',
      description: 'Incluye dashboards personalizados y reportes completos.'
    }, {
      id: 4,
      name: 'Full',
      description: 'Todas las funciones habilitadas.'
    }]);

    // Estados globales
    const searchTerm = ref('');
    const selectedIds = ref([]);
    const rowsPerPage = ref(5);
    const currentPage = ref(1);
    const showAddModal = ref(false);
    const showEditSidebar = ref(false);
    const editingCompany = ref({
      id: null,
      name: '',
      rut: '',
      contact: '',
      city: '',
      templateId: DEFAULT_TEMPLATE_ID
    });

    // Funciones
    const addCompany = company => {
      const id = Date.now();
      const createdAt = new Date().toISOString().slice(0, 10);
      customers.value.push({
        id,
        createdAt,
        ...company
      });
    };
    const deleteSelected = ids => {
      customers.value = customers.value.filter(c => !ids.includes(c.id));
      selectedIds.value = [];
    };
    const copyCustomerData = customer => {
      const data = `Nombre: ${customer.name}\nRUT: ${customer.rut}\nContacto: ${customer.contact}`;
      navigator.clipboard.writeText(data);
    };
    const editCustomer = customer => {
      editingCompany.value = {
        ...customer
      };
      if (!editingCompany.value.templateId) editingCompany.value.templateId = DEFAULT_TEMPLATE_ID;
      showEditSidebar.value = true;
    };
    const updateCompany = company => {
      const index = customers.value.findIndex(c => c.id === company.id);
      if (index !== -1) customers.value[index] = {
        ...company
      };
      showEditSidebar.value = false;
    };
    const __returned__ = {
      DEFAULT_TEMPLATE_ID,
      customers,
      templates,
      searchTerm,
      selectedIds,
      rowsPerPage,
      currentPage,
      showAddModal,
      showEditSidebar,
      editingCompany,
      addCompany,
      deleteSelected,
      copyCustomerData,
      editCustomer,
      updateCompany,
      ref,
      CompanyTable,
      AddCompanyModal,
      EditCompanySidebar
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
