import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import "core-js/modules/es.iterator.map.js";
import { ref, computed, watch } from 'vue';
import EmpresasTable from '@/components/EmpresasUi/EmpresasTable.vue';
import AgregarEmpresasModal from '@/components/EmpresasUi/AgregarEmpresasModal.vue';
import EditarEmpresasSideBar from '@/components/EmpresasUi/EditarEmpresasSideBar.vue';

// Datos y estado (los mismos que tenías)
const DEFAULT_TEMPLATE_ID = 1;
export default {
  __name: 'EmpresasView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const customers = ref([{
      id: 1,
      createdAt: '2025-07-30',
      name: 'Demo Combustible',
      rut: '76.145.230-9',
      contact: 'Marcelo Díaz',
      city: 'Santiago'
    }, {
      id: 2,
      createdAt: '2025-07-23',
      name: 'Industrial Glover S.P.A.',
      rut: '77.239.684-2',
      contact: 'Patricia González',
      city: 'Lautaro'
    }, {
      id: 3,
      createdAt: '2025-05-29',
      name: 'Cuenta Demo',
      rut: '76.003.412-5',
      contact: 'Claudia Muñoz',
      city: 'Temuco'
    }
    // ... demás clientes
    ]);
    const searchTerm = ref('');
    const selectedIds = ref([]);
    const selectAll = ref(false);
    const rowsPerPageOptions = [5, 10, 20, 50];
    const rowsPerPage = ref(5);
    const currentPage = ref(1);
    const showAddModal = ref(false);
    const step = ref(1);
    const selectedTemplate = ref(null);
    const showEditSidebar = ref(false);
    const editingCompany = ref({
      id: null,
      name: '',
      rut: '',
      contact: '',
      city: '',
      templateId: DEFAULT_TEMPLATE_ID
    });
    const newCompany = ref({
      name: '',
      rut: '',
      contact: '',
      city: ''
    });
    const copiedId = ref(null);

    // Plantillas
    const templates = ref([{
      id: 1,
      name: 'Básico',
      description: 'Acceso limitado a reportes y panel general.'
    }, {
      id: 2,
      name: 'Profesional',
      description: 'Incluye reportes y comentarios.'
    }, {
      id: 3,
      name: 'Avanzado',
      description: 'Incluye dashboards personalizados y reportes completos.'
    }, {
      id: 4,
      name: 'Full',
      description: 'Todas las funciones habilitadas.'
    }]);

    // Validación RUT chileno
    const isValidRut = rut => /^(\d{1,2}\.\d{3}\.\d{3}-[\dkK])$/.test(rut);

    // Filtrado y paginación
    const filteredCustomers = computed(() => {
      if (!searchTerm.value) return customers.value;
      const term = searchTerm.value.toLowerCase();
      return customers.value.filter(c => c.name.toLowerCase().includes(term) || c.rut.toLowerCase().includes(term) || c.contact.toLowerCase().includes(term) || c.createdAt.toLowerCase().includes(term));
    });
    const paginatedCustomers = computed(() => {
      const start = (currentPage.value - 1) * rowsPerPage.value;
      return filteredCustomers.value.slice(start, start + rowsPerPage.value);
    });
    const totalPages = computed(() => Math.ceil(filteredCustomers.value.length / rowsPerPage.value));
    const toggleSelectAll = () => selectAll.value ? selectedIds.value = paginatedCustomers.value.map(c => c.id) : selectedIds.value = [];
    const nextPage = () => {
      if (currentPage.value < totalPages.value) currentPage.value++;
    };
    const previousPage = () => {
      if (currentPage.value > 1) currentPage.value--;
    };

    // Modal multi-paso
    const goToNextStep = () => {
      if (isStep1Valid.value) step.value = 2;else alert('Completa todos los campos correctamente.');
    };
    const cancel = () => {
      showAddModal.value = false;
      step.value = 1;
      selectedTemplate.value = null;
      newCompany.value = {
        name: '',
        rut: '',
        contact: '',
        city: ''
      };
    };
    const addCompany = () => {
      if (!selectedTemplate.value) return;
      if (!isValidRut(newCompany.value.rut)) {
        alert('RUT inválido.');
        return;
      }
      const id = Date.now();
      const createdAt = new Date().toISOString().slice(0, 10);
      customers.value.push({
        id,
        createdAt,
        ...newCompany.value,
        templateId: selectedTemplate.value
      });
      cancel();
    };

    // Editar empresa
    const editCustomer = customer => {
      editingCompany.value = {
        ...customer
      };
      if (!editingCompany.value.templateId) editingCompany.value.templateId = DEFAULT_TEMPLATE_ID;
      showEditSidebar.value = true;
    };
    const updateCompany = () => {
      const index = customers.value.findIndex(c => c.id === editingCompany.value.id);
      if (index !== -1) customers.value[index] = {
        ...editingCompany.value
      };
      showEditSidebar.value = false;
    };

    // Copiar datos
    const copyCustomerData = customer => {
      const data = `Nombre: ${customer.name}\nRUT: ${customer.rut}\nContacto: ${customer.contact}`;
      navigator.clipboard.writeText(data).then(() => {
        copiedId.value = customer.id;
        setTimeout(() => copiedId.value = null, 1000);
      });
    };
    const isStep1Valid = computed(() => {
      const {
        name,
        rut,
        contact,
        city
      } = newCompany.value;
      return name.trim() && rut.trim() && contact.trim() && city.trim() && isValidRut(rut);
    });
    const modalButtonText = computed(() => step.value === 1 ? 'Siguiente' : 'Crear Empresa');
    const isModalButtonDisabled = computed(() => step.value === 2 && !selectedTemplate.value);
    watch(paginatedCustomers, () => selectAll.value = false);
    const __returned__ = {
      DEFAULT_TEMPLATE_ID,
      customers,
      searchTerm,
      selectedIds,
      selectAll,
      rowsPerPageOptions,
      rowsPerPage,
      currentPage,
      showAddModal,
      step,
      selectedTemplate,
      showEditSidebar,
      editingCompany,
      newCompany,
      copiedId,
      templates,
      isValidRut,
      filteredCustomers,
      paginatedCustomers,
      totalPages,
      toggleSelectAll,
      nextPage,
      previousPage,
      goToNextStep,
      cancel,
      addCompany,
      editCustomer,
      updateCompany,
      copyCustomerData,
      isStep1Valid,
      modalButtonText,
      isModalButtonDisabled,
      ref,
      computed,
      watch,
      EmpresasTable,
      AgregarEmpresasModal,
      EditarEmpresasSideBar
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
